import styled from 'styled-components';
   
export const Box = styled.div`
  padding: 10px 10px;
  background: black;
  position: absolute;
  bottom: 0;
  width: 100%;
  
   
  @media (max-width: 1000px) {
    padding: 70px 30px;
  }
`;
